import React from 'react';
import {useEffect,useRef} from 'react';
import './App.css';
import {BallMovement} from './BallMovement'
import ballObj from "./data"

const Canvas = () => {
	const canvasRef = useRef<HTMLCanvasElement | null>(null);
	console.log(canvasRef);

	useEffect(() => 
	{
		let ball = new ballObj(20, 200, 5, 5,20,10);
		const render = () => 
		{
			const canvas = canvasRef.current;
			if (!canvas)
			return;
			const context = canvas.getContext('2d');
			if (!context)
			return;
			//console.log(ball);
			context.clearRect(0,0,canvas.width, canvas.height);
			BallMovement(context, ball);
			if ( ball.y - ball.rad < 0 || ball.y + ball.rad > canvas.height
			)
			requestAnimationFrame(render);
		}
		render();
		/*
		const canvas = canvasRef.current;
		if(!canvas)
			return;
		const context = canvas.getContext('2d');
		if (!context)
			return;
		context.fillStyle = 'blue';
		context.fillRect(0,0,800,600);
		canvas.height = 100;*/
	}, []);
	return (<canvas id="canvas" ref={canvasRef}height="500px" width="800px"> </canvas>);
}

export default Canvas


