//export default(context:CanvasRenderingContext2D, canvas:HTMLCanvasElement, paddle:paddleObj) => {


export	class Paddle
	{
		x:number;
		y:number;
		height:number;
		width:number;
		color:string;
		v_y:number;
		lastKey:number;
		constructor(x:number, y:number, height:number, width:number, color:string, v_y:number, lastkey:number)
		{
			this.x = x;
			//this.y = canvas.height - 30;
			this.y = y;
			//this.height = 20;
			this.height = height;
			//this.width = paddle.width;
			this.width = width;
			//this.color = "white";
			this.color = color;
			this.v_y = v_y;
			//console.log(this.velocity_x);
		}
	}

export	function move(context:CanvasRenderingContext2D, canvas:HTMLCanvasElement, paddle:Paddle)
	{
		paddle.y += paddle.v_y;
		//console.log(paddle.velocity_x);
		//console.log(paddle.x);
		context.beginPath();
		context.rect(paddle.x, paddle.y, paddle.width, paddle.height);
		context.fillStyle = paddle.color;
		context.strokeStyle = "white";
		context.lineWidth = 1;
		context.shadowBlur = 0;
		context.shadowColor = "blue";
		context.strokeRect(paddle.x, paddle.y, paddle.width, paddle.height);
		context.fill();
	}

export interface keys
	{
		w:boolean;
		s:boolean;
	}

