import paddleObj from './data/paddleObj';




	class Paddle
	{
		context:CanvasRenderingContext2D;
		canvas:HTMLCanvasElement;
		x:number;
		y:number;
		height:number;
		width:number;
		color:string;
		velocity_x:number
		constructor(context:CanvasRenderingContext2D, canvas:HTMLCanvasElement, x:number, y:number, height:number, width:number, color:string, vel_x:number)
		{
			this.context = context;
			this.canvas = canvas;
			this.x = x;
			//this.y = canvas.height - 30;
			this.y = y;
			//this.height = 20;
			this.height = height;
			//this.width = paddle.width;
			this.width = width;
			//this.color = "white";
			this.color = color;
			this.velocity_x = vel_x;
			//console.log(this.velocity_x);
		}
	move()
	{
		this.x += this.velocity_x;
		//console.log(this.velocity_x);
		//console.log(this.x);
		this.context.beginPath();
		this.context.rect(this.x, this.y, this.width, this.height);
		this.context.fillStyle = this.color;
		this.context.strokeStyle = "white";
		this.context.lineWidth = 1;
		this.context.shadowBlur = 0;
		this.context.shadowColor = "blue";
		this.context.strokeRect(this.x, this.y, this.width, this.height);
		this.context.fill();
	}
}
